-- Create "sarifs" table
CREATE TABLE `sarifs` (`id` char(36) NOT NULL, `report_file_name` varchar(255) NOT NULL, `statement_sarif` char(36) NOT NULL, PRIMARY KEY (`id`), INDEX `sarif_report_file_name` (`report_file_name`), UNIQUE INDEX `statement_sarif` (`statement_sarif`), CONSTRAINT `sarifs_statements_sarif` FOREIGN KEY (`statement_sarif`) REFERENCES `statements` (`id`) ON UPDATE NO ACTION ON DELETE NO ACTION) CHARSET utf8mb4 COLLATE utf8mb4_bin;
-- Create "sarif_rules" table
CREATE TABLE `sarif_rules` (`id` char(36) NOT NULL, `rule_id` varchar(255) NOT NULL, `rule_name` varchar(255) NOT NULL, `short_description` varchar(255) NOT NULL, `sarif_sarif_rules` char(36) NULL, PRIMARY KEY (`id`), UNIQUE INDEX `sarif_sarif_rules` (`sarif_sarif_rules`), INDEX `sarifrule_rule_id` (`rule_id`), INDEX `sarifrule_rule_name` (`rule_name`), INDEX `sarifrule_short_description` (`short_description`), CONSTRAINT `sarif_rules_sarifs_sarif_rules` FOREIGN KEY (`sarif_sarif_rules`) REFERENCES `sarifs` (`id`) ON UPDATE NO ACTION ON DELETE SET NULL) CHARSET utf8mb4 COLLATE utf8mb4_bin;
