-- Create "git_attestations" table
CREATE TABLE `git_attestations` (`id` char(36) NOT NULL, `commit_hash` varchar(255) NOT NULL, `author` varchar(255) NOT NULL, `author_email` varchar(255) NOT NULL, `committer_name` varchar(255) NOT NULL, `committer_email` varchar(255) NOT NULL, `commit_date` varchar(255) NOT NULL, `commit_message` varchar(255) NOT NULL, `status` json NOT NULL, `commit_type` varchar(255) NOT NULL, `commit_digest` varchar(255) NOT NULL, `signature` varchar(255) NOT NULL, `parent_hashes` json NOT NULL, `tree_hash` varchar(255) NOT NULL, `refs` json NOT NULL, `remotes` json NOT NULL, `attestation_git_attestation` char(36) NOT NULL, PRIMARY KEY (`id`), UNIQUE INDEX `attestation_git_attestation` (`attestation_git_attestation`), CONSTRAINT `git_attestations_attestations_git_attestation` FOREIGN KEY (`attestation_git_attestation`) REFERENCES `attestations` (`id`) ON UPDATE NO ACTION ON DELETE NO ACTION) CHARSET utf8mb4 COLLATE utf8mb4_bin;
