-- Create "git_attestations" table
CREATE TABLE "git_attestations" ("id" uuid NOT NULL, "commit_hash" character varying NOT NULL, "author" character varying NOT NULL, "author_email" character varying NOT NULL, "committer_name" character varying NOT NULL, "committer_email" character varying NOT NULL, "commit_date" character varying NOT NULL, "commit_message" character varying NOT NULL, "status" jsonb NOT NULL, "commit_type" character varying NOT NULL, "commit_digest" character varying NOT NULL, "signature" character varying NOT NULL, "parent_hashes" jsonb NOT NULL, "tree_hash" character varying NOT NULL, "refs" jsonb NOT NULL, "remotes" jsonb NOT NULL, "attestation_git_attestation" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "git_attestations_attestations_git_attestation" FOREIGN KEY ("attestation_git_attestation") REFERENCES "attestations" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create index "git_attestations_attestation_git_attestation_key" to table: "git_attestations"
CREATE UNIQUE INDEX "git_attestations_attestation_git_attestation_key" ON "git_attestations" ("attestation_git_attestation");
